// Portfolio Gallery Page - Enhanced UX
// Version: 3.5
// Date: 2025-06-21
// Description: Adds pulsing loading animation and corrects misleading "No images" message

import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import Lightbox from 'yet-another-react-lightbox';
import Fullscreen from 'yet-another-react-lightbox/plugins/fullscreen';
import Zoom from 'yet-another-react-lightbox/plugins/zoom';
import 'yet-another-react-lightbox/styles.css';

export default function GalleryPage() {
  const { category } = useParams();
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [lightboxIndex, setLightboxIndex] = useState(-1);

  useEffect(() => {
    const loadImages = async () => {
      setLoading(true);
      const categoryPath = `/categories/${category}`;
      const context = require.context(`../public/categories/${category}`, false, /\.(png|jpe?g|webp)$/);
      const imagePaths = context.keys().map(key => `${categoryPath}/${key.replace('./', '')}`);
      setImages(imagePaths);
      setLoading(false);
    };

    try {
      loadImages();
    } catch (error) {
      console.error('Failed to load images for category:', category, error);
      setImages([]);
      setLoading(false);
    }
  }, [category]);

  return (
    <div className="min-h-screen bg-gray-950 text-white px-4 py-10">
      <div className="max-w-6xl mx-auto">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-4xl font-bold capitalize">{category.replace(/-/g, ' ')}</h1>
          <Link
            to="/"
            className="text-sm text-gray-400 hover:text-white border border-gray-700 px-3 py-1 rounded-lg"
          >
            ‚Üê Back to Home
          </Link>
        </div>

        {loading ? (
          <div className="flex justify-center items-center h-96">
            <div className="flex space-x-2">
              <div className="w-4 h-4 bg-white rounded-full animate-pulse" />
              <div className="w-4 h-4 bg-white rounded-full animate-pulse delay-100" />
              <div className="w-4 h-4 bg-white rounded-full animate-pulse delay-200" />
            </div>
          </div>
        ) : images.length > 0 ? (
          <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
            {images.map((src, idx) => (
              <img
                key={idx}
                src={src}
                alt={`Render ${idx + 1}`}
                className="w-full h-48 object-cover rounded-lg cursor-zoom-in hover:opacity-90 transition"
                onClick={() => setLightboxIndex(idx)}
              />
            ))}
          </div>
        ) : (
          <p className="text-center text-gray-500">No images found for this category.</p>
        )}
      </div>

      <Lightbox
        open={lightboxIndex >= 0}
        close={() => setLightboxIndex(-1)}
        index={lightboxIndex}
        slides={images.map(src => ({ src }))}
        plugins={[Fullscreen, Zoom]}
      />
    </div>
  );
}
